"use strict";

var utils = require('syracuse-tablet/html/js/helpers/utils');
var log = require('syracuse-tablet/html/js/helpers/logger').getLogger("ctrl");
var Control = require('syracuse-tablet/html/js/controls/ctrlBase').Control;

var _Klass = utils.defineClass(
	function(controller, article, prototype) {
		var self = this;
		Control.call(self, controller, article, prototype);
	}, Control, {
		destroy: function() {
			var self = this;
			Control.destroy.call(self);
		}
	});

exports.Klass = _Klass;