"use strict";

var utils = require('syracuse-tablet/html/js/helpers/utils');
var Base = require('syracuse-tablet/html/js/controls/ctrlFieldBase').Klass;
var uiUtils = require('syracuse-tablet/html/js/ui/uiUtils');

var _Klass = utils.defineClass(

	function(controller, article, prototype) {
		var self = this;
		Base.call(self, controller, article, prototype);
	}, Base, {
		buildHtml: function(parentSlot, controllerDao, buildOptions) {
			var self = this;
			buildOptions = buildOptions || {};
			self.dao = controllerDao;
			var fieldSlot = uiUtils.createDomElement("div", ["sm-field-slot"], null, {
				"id": this.id
			});
			// in edit mode, value is displayed before title
			if (this.$isEditMode) {
				this.buildFieldTitle(fieldSlot, buildOptions);
				this.buildFieldCore(fieldSlot, controllerDao, buildOptions);
			} else {
				this.buildFieldTitle(fieldSlot, buildOptions);
				this.buildFieldCore(fieldSlot, controllerDao, buildOptions);
			}

			parentSlot.appendChild(fieldSlot);
		},
		buildFieldValue: function(parentSlot, buildOptions) {
			var self = this;
			if (this.$isEditMode) {
				parentSlot.appendChild(uiUtils.createDomElement("input", ["sm-field-input-boolean"], null, {
					"type": "checkbox",
					"checked": self.dao.getValue(this.article.$bind) ? true : false
				}));
			} else {
				parentSlot.appendChild(uiUtils.createDomElement("span", ["glyphicon", self.dao.getValue(this.article.$bind) ? "glyphicon-ok" : "glyphicon-remove"]));
			}
		}
	});

exports.Klass = _Klass;