"use strict";

var utils = require('syracuse-tablet/html/js/helpers/utils');
var templates = require('syracuse-tablet/html/js/helpers/templating');

/**
 * Page header Generate Html
 */
var _Klass = utils.defineClass(
	function(page, prototype, $article) {
		var self = this;
		self.prototype = prototype;
		self.$article = $article;
		self.page = page;
	}, null, {

		destroy: function() {
			var self = this;
			self.prototype = null;
			self.$article = null;
			self.page = null;
			if (!self.$elmt) return;
			utils.unbindObj(self.$elmt);
			self.$elmt = null;
			utils.unbindObj(self);
		},

		loadStructure: function() {

		},

		buildHtml: function(parent, controllerDao, buildOptions) {
			var self = this;
			var header =
				'<nav class="navbar navbar-inverse" role="navigation">' +
				'<div class="container-fluid">' +
				'<div class="navbar-header">' +
				'<button type="button" class="navbar-toggle" data-toggle="collapse"' +
				'	data-target="#header-coll{{togglenav}}</span> <span' +
				'		class="icon-bar"></span> <span class="icon-bar"></span> <span' +
				'		class="icon-bar"></span>' +
				'</button>' +
				'<a class="navbar-brand" href="#">Sage ERP X3 - Mobile</a>' +
				'</div>' +
				'<div class="collapse navbar-collapse" id="#header-collapse-' + self.page.id + '">' +
				'<ul class="nav navbar-nav navbar-right">' +
				'<li><a href="#" data-action="logout">' +
				'<span class="glyphicon glyphicon-log-out" />Logout</a></li>' +
				'</ul>' +
				'</div>' +
				'</div>' +
				'</nav>';

			var h = $(header);
			parent.appendChild(h[0]);
		}
	});

exports.Klass = _Klass;