"use strict";

var utils = require('syracuse-tablet/html/js/helpers/utils');
var log = require('syracuse-tablet/html/js/helpers/logger').log;
var Control = require('syracuse-tablet/html/js/helpers/control').Control;


var _Control = utils.defineClass(
	function(controller, type, options) {
		Control.call(this, controller, type, options);
	}, Control, {
		destroy: function() {
			Control.destroy.call(this);
		}
	});

exports.Control = _Control;