"use strict";

var utils = require('syracuse-tablet/html/js/helpers/utils');
var log = require('syracuse-tablet/html/js/helpers/logger').getLogger("ctrl");
var Base = require('syracuse-tablet/html/js/controls/layoutBase').Klass;
var BuilderLytRowHtml = require('syracuse-tablet/html/js/controls/builderLytHtml').BuilderLytRowHtml;

var _defDisplayOptions = {
	cssDefLayout: ["container s-m-layout s-m-row"],
	cssDefCell: ["s-m-layout-cell"],
	cssDefRow: ["row"]
};
/**
 * cssOptions
 * 		cssLayout, cssRow, cssCell: class added to default ones
 * 		rowHeight to fix the height of the row
 * default classes can  be overridden
 */
var _Klass = utils.defineClass(

	function(controller, type, article) {
		var self = this;
		Base.call(self, controller, type, article);
	}, Base, {
		defaultDisplayOptions: function() {
			return _defDisplayOptions;
		}
	});

exports.Klass = _Klass;