"use strict";

var utils = require('syracuse-tablet/html/js/helpers/utils');
var Base = require('syracuse-tablet/html/js/controls/layoutBase').Klass;

var _Klass = utils.defineClass(
	function(controller, type, article) {
		var self = this;
		Base.call(self, controller, type, article);
	}, Base, {

		buildHtmlBeforeChild: function(html, data, child, idx) {
			var self = this;
			html.push('<div class="');
			html.push(' s-m-layout-cell">');
		},

		buildHtmlAfterChild: function(html, data, child, idx) {
			var self = this;
			html.push('</div>');
		},

		buildHtmlBefore: function(html, data) {
			var self = this;
			html.push('<div class="container s-m-layout s-m-stack" id="');
			html.push(self.id);
			html.push('">');
		},

		buildHtmlAfter: function(html, data) {
			var self = this;
			html.push('</div>');
		}
	});

exports.Klass = _Klass;