"use strict";

var Base = require('syracuse-tablet/html/js/controls/panels/aside/panelAside').PanelAside;
var locale = require('syracuse-tablet/html/js/helpers/locale');
var utils = require('syracuse-tablet/html/js/helpers/utils');

var _availableSlots = ["home", "actions", "links", "tools", "unclassified"]
	/**
	 * 
	 */
exports.PanelActions = utils.defineClass(
	function PanelActions(controller, type, article, opts) {
		Base.call(this, controller, type, article, opts);
	}, Base, {
		_getItems: function() {
			return this.actionAdapter.getActionsPanelItems();
		},
		_extendContext: function(ctx) {
			ctx.title = locale.text("actions");
			return ctx
		},
		_getAvailableSlots: function() {
			return _availableSlots
		}
	});