"use strict";

var log = require('syracuse-tablet/html/js/helpers/logger').getLogger("vignette");
var utils = require('syracuse-tablet/html/js/helpers/utils');
var Base = require('syracuse-tablet/html/js/controls/ctrlBase').Klass;
var pageHelper = require('syracuse-tablet/html/js/application/pageHelper');

var _Klass = utils.defineClass(
	function(controller, article, prototype) {
		var self = this;
		Base.call(self, controller, article, prototype);
	}, Base, {
		destroy: function() {
			var self = this;
			Base.prototype.destroy.call(self);
		},

		buildHtmlBefore: function(html) {
			var self = this;
			html.push('<div class="s-m-vignette" id="');
			html.push(self.id);
			html.push('">');
			//html.push('<h1>' + self.typeName + '</h1><p>' + self.id + '</p>');
			return html;
		},
		buildHtmlAfter: function(html) {
			var self = this;
			html.push('</div>');
			return html;
		},
		/**
		 * Loaded by dashboard.afterRender
		 * 	temporarily we generate a link to the page
		 *  load_save load the content - TODO connect later
		 **/
		load: function() {
			var self = this;
			var state;
			var deferred = $.Deferred();
			var rejectError = function(e) {
				var msg = "Vignette page [" + (state ? state.name : "?") + "] loading error";
				log && log(msg, e);
				// !! TODO - deferred - this deferred is not correctly rejected - dash-board reject callback is not called - To test
				alert(msg);
				deferred.reject(e);
			};
			try {
				state = self._getPageInfo();
				if (state.name.smEndsWith("$query")) {
					var dstPage = factory.createPage(self.$elmt, state);
					dstPage.load().then(function() {
						dstPage.activate(null, function() {
							deferred.resolve();
						});
					}, rejectError);
				} else {
					var html = [];
					html.push('<div>');
					html.push('<a href="#" data-nav="');
					html.push(state.name);
					html.push('">');
					html.push(state.name);
					html.push('</a>');
					html.push('</div>');
					self.$elmt.html(html.join(''));
					deferred.resolve();
				}
			} catch (e) {
				rejectError(e);
			} finally {
				return deferred.promise();
			}
		},

		/**
		 * returns the page.pageInfo of the gadget's page
		 **/
		_getPageInfo: function() {
			var self = this;
			var gadget = self.prototype.data("$gadget");
			if (gadget.$type === "$representation") {
				var name = gadget.representation + "." + gadget.facet;
				var page = pageHelper.getPageInfo(name, {
					vignette: true
				});
				if (page) return page;
			}
			return reqPage.getPageInfo("vignetteerror", {
				vignette: true
			});
		}
	});

exports.Klass = _Klass;