"use strict";

var clientContract = require('syracuse-tablet/html/js/sdata/entities/clientContract');
var demoUtils = require('syracuse-tablet/html/js/demos/utils');
var dashOverview = require('syracuse-tablet/html/js/demos/salesoverview/dashOverview');
var dashCustomer = require('syracuse-tablet/html/js/demos/salesoverview/dashCustomer');
var entities = require('syracuse-tablet/html/js/demos/_entities/main');

var _demoApp = {
	"$application": {
		"$uuid": "$demoApp",
		"applicationName": "$demoApp",
		"title": "Sales overview",
		"description": "Sales overview",
		"iconName": "money",
		"$homeDashboard": {
			"$uuid": "$demoAppOverviewDashboard",
			"dashboardName": "$demoAppOverviewDashboard"
		}
	},
	"$dashboards": {},
	"$gadgets": {}
};

exports.register = function() {
	entities.registerAll();
	demoUtils.addToApp(_demoApp, dashOverview);
	demoUtils.addToApp(_demoApp, dashCustomer);
	clientContract.registerApp(_demoApp);
};