"use strict";

var winjs = require('syracuse-tablet/html/js/helpers/winjs');

var _log;

if (winjs.isAvailable()) {
	_log = function() {
		var args = [];
		for (var i = 0; i < arguments.length; i++) {
			args.push(arguments[i]);
		}
		winjs.callWinJS("logger", "log", {
			"args": args
		}, true);
	};
} else {
	_log = function() {
		var text = Array.prototype.join.call(arguments, "");
		console && console.log && console.log(text);
	};
}
if (window) {
	window.logger = _log;
}

function _getLogger(module) {
	var logger = function() {
		var text = "[" + module + "]: ";
		for (var i = 0; i < arguments.length; i++) {
			text = text + arguments[i];
		}
		_log(text);
	};
	return logger;
}
exports.log = _log;
exports.getLogger = _getLogger;