"use strict";

var log = require('syracuse-tablet/html/js/helpers/logger').getLogger("nativeLifeTile", true);

var _currentDeferred;
//Global object to be called by native code on pencil events
if (!(window.smLifeTileJS)) {
	window.smLifeTileJS = {

		/*
		 *  This method will be invoked from wrapper when there was a life tile action executed.
		 *  data {
		 *  	action :"add" //"remove" "get"
		 *  	content:{ (null in case of get if not tile found)
		 *  			id:"id passed",
		 *  			title:"my customers",
		 *  			...
		 * 		}
		 *  	error:{ (if error occurs)
		 *  		no:"132001"
		 *  		text:"Not found", "unexpected error" ...
		 *  	}
		 *  }
		 */
		lifeTileActionFinished: function(data) {
			if (_currentDeferred) {
				var def = _currentInputDeferred;
				_currentDeferred = null;
				log && log("Command life tile finished: " + JSON.stringify(data || {}));
				def.resolve(data);
			}
		}
	};
}
