exports.actionLifeTile = function(options) {
	var data = JSON.stringify(options);
	if (window && window.smLiveTile && window.smLiveTile.actionLiveTile) {
		_currentDeferred = $.Deferred();
		window.smLiveTile.actionLiveTile(data);
		return _currentDeferred.promise();
	} else {
		/*test
		if(options.action=="get"){
			return $.smResolve({action:options.action,content:null})
		}
		if(options.action=="remove"){
			return $.smResolve({action:options.action,content:{id:options.content.id},error:{no:"121212",text:"plouuffff"}})
		}
		End test*/
		return $.smResolve(null);
	}
};
exports.supportsLifeTile = function() {
	var supported = (window && window.smLiveTile && window.smLiveTile.supportsLiveTile && window.smLiveTile.supportsLiveTile()) || false;
	log && log("Life tile : " + supported);
	return supported;
};
/**
 *
 */
exports.addLifeTile = function(options) {
	return exports.actionLifeTile({
		action: "add",
		content: options
	});
};

/**
 *
 */
exports.removeLifeTile = function(options) {
	return exports.actionLifeTile({
		action: "remove",
		content: {
			id: options
		}
	});
};
exports.getLifeTile = function(options) {
	return exports.actionLifeTile({
		action: "get",
		content: {
			id: options
		}
	});
};