"use strict";

var _TestNativeWrapper = function() {
	this.scanBarcodeSupported = function() {
		return true;
	};
	this.scanBarcode = function(options) {
		setTimeout(function() {
			var data = {
				Action: "ok",
				Content: "Barcode",
				Format: "QR_CODE"
			};
			window.smBarcodeJS.scanBarcodeFinished(data);
		}, 100);
	};
};

exports.create = function() {
	return new _TestNativeWrapper();
};