"use strict";

exports.isSageX3WUPApp = function() {
	return window && window.smNativeApp;
};

/*
 * Initiates the native app to show a page to select another server
 */
exports.configServer = function() {
	return window &&
		window.smNativeApp &&
		window.smNativeApp.configServer &&
		window.smNativeApp.configServer();

};
/*
 * Initiates the native app to show a page to select another server
 */
exports.notifLoaded = function() {
	return window &&
		window.smNativeApp &&
		window.smNativeApp.notifLoaded &&
		window.smNativeApp.notifLoaded();

};

exports.notifStartFail = function(msg) {
	return window &&
		window.notifStartFail &&
		window.notifStartFail.notifLoaded &&
		window.notifStartFail.notifLoaded(msg);
};