"use strict";

var log = require('syracuse-tablet/html/js/helpers/logger').getLogger("protoParser");
var utils = require('syracuse-tablet/html/js/helpers/utils');

/**
 * Parses a JSON prototype and returns a default JSON $article
 *  	prototype: JSON
 */
var _proto2Article = function(prototype) {
	var props = prototype.$properties;
	var article = {
		$layoutType: "stack",
		$items: []
	};
	for (var prop in props) {
		article.$items.push({
			$bind: prop
		});
	}
	return article;
};

//exports.array2Article = _array2Article;
exports.proto2Article = _proto2Article;