//This function is only allowed on user interaction (e.g. click/touch)
exports.toggleFullScreen = function() {
	/** FDB - Disabled because when the fullscreen is enabled on android there's no resize event when keyboard is shown/hidden
	 * As a result we can't use the login page in landscape mode because SignIn button in not reachable (we've to clos ethe keyboard manually
	 * I also doesn't work on IOS
	 **/
	return;
	try {
		if (!exports.isDesktop()) {
			var doc = window.document;
			var docEl = doc.documentElement;
			var requestFullScreen = docEl.requestFullscreen || docEl.mozRequestFullScreen || docEl.webkitRequestFullScreen || docEl.msRequestFullscreen;
			var cancelFullScreen = doc.exitFullscreen || doc.mozCancelFullScreen || doc.webkitExitFullscreen || doc.msExitFullscreen;
			requestFullScreen && requestFullScreen.call(docEl);
		}
	} catch (e) {}
};