	return $.smResolve()
		.then(function() {
			var pageDetails = {
				prototype: pageInfo.rowDetailPrototype
			};
			return _ensureRegularPageDetailsArticle(pageInfo, pageDetails)
				.then(function() {
					return pageDetails;
				});
		});
