	dashboard.$vignettes = {
		"testLayoutsDashboard": {
			"$uuid": "testLayoutsDashboard",
			"$displayStyle": "$link"
		}
	};
	dashboard.$article = {
		"$layoutType": "stack",
		"$items": [{
			"$layoutType": "row",
			"displayOptions": {
				"rowHeight": "150px"
			},
			"$widthXs": "1",
			"$items": [{
				"$bind": "testLayoutsDashboard"
			}]
		}]
	};

	_welcomeApplication.$gadgets = {};
	dashboard.$vignettes = {};
	dashboard.$article = {
		"$layoutType": "hub",
		"$items": [
			/* {
				"$layoutType": "hubGroup",
				"$title": "Gadgets",
				"$items": []
			}, */
			{
				"$layoutType": "hubGroup",
				"$title": "Applications",
				"$items": []
			}
		]
	};

