"use strict";

var Base = require('syracuse-tablet/html/js/pages/actionAdapter').ActionAdapter;
var utils = require('syracuse-tablet/html/js/helpers/utils');

/**
 * 
 */
exports.ActionAdapterPageDrafts = utils.defineClass(
	function ActionAdapterPageDrafts(page) {
		Base.call(this, page);
		this.showHome = true;
	},
	Base, {
		destroy: function() {
			Base.prototype.destroy.call(this);
		},
		getHeaderItems: function() {
			var items = [];
			return items;
		},
		getActionsPanelItems: function() {
			var items = [];
			if (this.showHome) {
				this._addHomeButton(items, this.page.controller, {
					slot: "home"
				});
			}
			return items;
		}
	});