"use strict";
/**
 * Default Home page that is display if there's no current mobile application
 * User can select an application or logout
 */
var utils = require('syracuse-tablet/html/js/helpers/utils');
var Base = require('syracuse-tablet/html/js/pages/html/pageHtml').Page;

var _tmplCtx = {
	content: {
		"title": "Home page",
		"listtitle": "Please select an application",
		"listcontent": ""
	}
};
var _Page = utils.defineClass(
	function($parent, state) {
		var self = this;
		Base.call(self, $parent, state);
	}, Base, {

		buildHtml: function() {
			var self = this;
			var error = (self.state.options && self.state.options.error) || "";
			var cc = self.$elmt.is("section") ? self.$elmt : self.$elmt.children("section");
			cc.html("<H1>VIGNETTE ERROR</H1>" + error);
			return $.smResolve();
		}
	});

exports.Page = _Page;