"use strict";

var utils = require('syracuse-tablet/html/js/helpers/utils');
var log = require('syracuse-tablet/html/js/helpers/logger').log;
var Base = require('syracuse-tablet/html/js/pages/page').Page;
var templates = require('syracuse-tablet/html/js/pages/pageTemplates');
var auth = require('syracuse-tablet/html/js/application/authentication');

var _tmplCtx = {
	header: {
		pageId: "login"
	},
	content: {
		"title": "Home page"
	}
};
var _Page = utils.defineClass(
	function($parent, state) {
		var self = this;
		Base.call(self, $parent, state);
	}, Base, {

		buildHtml: function(state, data) {
			var self = this;
			var deferred = $.Deferred();
			try {
				templates.getTemplate(self.state, _tmplCtx.header, _tmplCtx.content).always(function(result) {
					deferred.resolve(result);
				});
			} catch (e) {
				templates.resolveError(deferred, self.state.name, e);
			} finally {
				return deferred.promise();
			}
		}

	});

exports.Page = _Page;