"use strict";

var utils = require('syracuse-tablet/html/js/helpers/utils');
var globals = require('syracuse-tablet/html/js/helpers/globals');
var reqLocale = require('syracuse-tablet/html/js/helpers/locale');
var log = require('syracuse-tablet/html/js/helpers/logger').getLogger("page");
var Base = require('syracuse-tablet/html/js/pages/page').Page;
var pageTemplates = require('syracuse-tablet/html/js/pages/pageTemplates');
var auth = require('syracuse-tablet/html/js/application/authentication');
var dispatcher = require('syracuse-tablet/html/js/sdata/sdataDispatcher');
var notifications = require('syracuse-tablet/html/js/helpers/notifications');

var _tmplCtx = {
	header: {
		"pageId": "login",
		"headerttl": "Sage X3 Mobile Login Page",
		"forgotpwd": "Forgot password"
	},
	content: {
		"label_user": "User name",
		"placeholder_user": "Enter some text",
		"label_password": "Password",
		"placeholder_password": "Enter some text",
		"act_login": "Login",
		"label_lang": "Language",
		"title": "Login page"
	}
};
var lang;
var _Page = utils.defineClass(
	function($parent, state) {
		var self = this;
		Base.call(self, $parent, state);
	}, Base, {

		buildHtml: function(data) {
			var self = this;
			var deferred = $.Deferred();
			try {
				dispatcher.queryLocalePreferences().always(function(locales) {
					var html = [];
					(locales || []).forEach(function(locale) {
						html.push('<option value="');
						html.push(locale.code);
						html.push('"');
						if (reqLocale.isCurrent(locale.code)) {
							html.push(' selected');
						}
						html.push('>');
						html.push(locale.description);
						html.push('</option>');
					});
					_tmplCtx.content.locales = html.join('\n');
					_tmplCtx.content.reason = self.getProp("reason") || "";
					pageTemplates.getTemplate(self.state, _tmplCtx.header, _tmplCtx.content).always(function(result) {
						deferred.resolve(result);
					});
				});
			} catch (e) {
				pageTemplates.resolveError(deferred, self.state.name, e);
			} finally {
				return deferred.promise();
			}
		},

		_actLogin: function() {
			var self = this;
			var success = self.$elmt.find("#login-success").hide();
			var fail = self.$elmt.find("#login-fail").hide();
			var usr = self.$elmt.find("#login-user").val();
			var pwd = self.$elmt.find("#login-password").val();
			auth.login(usr, pwd).always(function(ok, userProfile, message) {
				if (ok) {
					success.show().html("Login succeeded");
					window.setTimeout(function() {
						// self.getProp("redirect") - not implemented
						notifications.publish("sm.login", userProfile);
					}, 1000);
				} else {
					fail.show().html("Login failed<p>" + message + "</p>");
				}

			});
		}
	});

exports.Page = _Page;