"use strict";

var _DataSet = utils.defineClass(function() {}, null, {
	init: function(data, prototype, options) {
		this.options = options || {};
		this.data = data || {};
		this.prototype = prototype || {};
		this.$properties = this.prototype.$properties || {};
	},
	destroy: function() {},
	value: function(prop, value) {
		if (value != null) {
			// TODO - eventually check proto rules
			return this.data[prop] = value;
		}
		return this.data[prop];
	},
	propValue: function(prop) {
		return {
			val: this.value(prop),
			prop: this.$properties[prop]
		};
	}
});
exports.DataSet = _DataSet;
exports.create = function(data, options) {
	var o = new _DataSet();
	o.init(data, options);
	return o;
};