"use strict";

var datasetUtils = require('syracuse-tablet/html/js/sdata/datasetUtils');

/**
 * Load submodules to avoid dependencies
 */
// First
exports.Dataset = require('syracuse-tablet/html/js/sdata/datasetBase').DatasetBase;
// Next
// !! use datasetUtils.getImplementation("className") to get the class
datasetUtils.setImplementation(require('syracuse-tablet/html/js/sdata/datasetArrayRow').RowDataset);
datasetUtils.setImplementation(require('syracuse-tablet/html/js/sdata/datasetArrayMulti').MultiArrayDataset);
datasetUtils.setImplementation(require('syracuse-tablet/html/js/sdata/datasetArraySingle').SingleArrayDataset);
exports.createFromDraft = function(draftData, prototype) {
	var dataset = new exports.Dataset({}, prototype);
	dataset.setDraftsData(draftData);
	return dataset;
}