"use strict";

var _apps = [];

exports.getApps = function() {
	var apps = [];
	_apps.forEach(function(app) {
		apps.push(app.$application);
	});
	return apps;
};

exports.getAppDetail = function(applicationName) {
	var match;
	_apps.some(function(app) {
		if (app.$application.applicationName === applicationName) {
			match = app;
			return true;
		}
		return false;
	});
	return match;
};

exports.registerApp = function(app) {
	_apps.push(app);
};