var _getIconByName = function(name) {
	switch (name) {
		case "$multiSelection":
			return "glyphicon glyphicon-list-alt";
		case "$cancelMultiSel":
		case "$cancelEdit":
			return "fa fa-undo";
		case "$edit":
			return "fa fa-edit";
		case "$query":
			return "fa fa-list";
		case "$save":
			return "fa fa-save";
		case "$details":
			return "fa fa-level-down";
		case "$delete":
			return "fa fa-trash-o";
		case "$create":
			return "fa fa-plus-square";
			break;
		case "$image-sign":
			return "fa fa-pencil";
			break;
		case "$image-camera":
			return "fa fa-camera";
			break;
		case "$image-ink":
			return "fa fa-paint-brush";
			break;
		case "appUpdate":
			return "fa fa-history";
			break;
		case "appSetup":
			return "fa fa-download";
			break;
		case "gear":
			return "fa fa-gear";
			break;

	}
	// Icon names used for tiles or tile headers
	if (_tileIcons.indexOf(name) > -1) {
		return "fa fa-" + name;
	}

	return "";
};

exports.getIconByName = _getIconByName;
exports.checkBoxIcon = function(val) {
	return ["glyphicon", val ? "glyphicon-ok" : "glyphicon-remove"];
};
exports.dateFieldIcon = function($type) {
	return $type.indexOf("date") != -1 ? ["glyphicon", "glyphicon-calendar"] : ["glyphicon", "glyphicon-time"];
};
