exports.layoutExpandCollapseIcon = function(collapse, advancedTheme) {
	if (collapse) {
		return advancedTheme ? "fa fa-ellipsis-h" : "fa fa-chevron-down";
	} else {
		return advancedTheme ? "fa fa-times" : "fa fa-chevron-up";
	}
};
