var _getIconByIdx = function(idx) {
	if (idx == null) {
		idx = Math.random() * (_fontAwesome.length - 1);
	} else if (idx < _fontAwesome.length) {
		idx = Math.abs(idx);
	} else {
		var x = Math.round(Math.abs(idx) / _fontAwesome.length);
		idx = idx - x * _fontAwesome.length;
	}
	return "fa " + _fontAwesome[idx];

};
// TODO getIconByName name= home, save, edit... 

var _getIconByName = function(name) {
	switch (name) {
		case "$edit":
			return "fa fa-pencil-square-o";
		case "$query":
			return "fa fa-list";
		case "$save":
			return "fa fa-floppy-o";
		case "$details":
			return "fa fa-level-down";
		case "$delete":
			return "fa fa-trash-o";
		case "$create":
			return "fa fa-plus-square";
			break;
		default:
			console.log("Icon name=" + name + " not implemented yet");
			return "fa fa-warning";
			break;
	}
	return true;
};

exports.getIconByIdx = _getIconByIdx;
exports.getIconByName = _getIconByName;