"use strict";

var utils = require('syracuse-tablet/html/js/helpers/utils');
var Base = require('syracuse-tablet/html/js/ui/modals/modalMultiButtons').Modal;
var locale = require('syracuse-tablet/html/js/helpers/locale');


var _Klass = utils.defineClass(
	function ModalMultiButtons(title, message) {
		var opts = {
			buttons: []
		};
		["cancel", "no", "yes"].forEach(function(act) {
			opts.buttons.push({
				action: act,
				label: locale.text(act)
			});
		});
		opts.defaultAction = "cancel";
		Base.call(this, title, message, opts);
	}, Base, {});
exports.Modal = _Klass;