"use strict";

var Base = require('syracuse-tablet/html/js/wc/controls/ctrlSdataBase').CtrlSdataBase;
var utils = require('syracuse-tablet/html/js/wc/helpers/utils');

exports.CtrlSingleArray = utils.defineClass(
	function CtrlSingleArray(controller, article, prototype, options) {
		Base.call(this, controller, article, prototype, options);
	}, Base, {
		destroy: function() {
			Base.prototype.destroy.call(this);
		},

		buildHtml: function() {
			if (!this.$$elmt) {
				Base.prototype.buildHtml.call(this, ["s-m-array", "s-m-single"]);
			}
			this.$$elmt.html("<b>this is a single array</b>")
		}

	});