"use strict";

var modules = require('syracuse-tablet/html/js/wc/common/modules');

//globally used modules
var navigationHelper = require('syracuse-tablet/html/js/wc/app/navigationHelper');
var navigationPageStack = require('syracuse-tablet/html/js/wc/app/navigationPageStack');
var ctrlFactory = require('syracuse-tablet/html/js/wc/controls/ctrlFactory');
var pageFactory = require('syracuse-tablet/html/js/wc/pages/pageFactory');
var controller = require('syracuse-tablet/html/js/wc/controllers/controller');
var modal = require('syracuse-tablet/html/js/wc/ui/modals/modal');
var siteLayout = require('syracuse-tablet/html/js/wc/ui/siteLayout');
var appController = require('syracuse-tablet/html/js/wc/app/appController');
var ajax = require('syracuse-tablet/html/js/wc/common/ajax');
var session = require('syracuse-tablet/html/js/wc/app/session');
var storage = require('syracuse-tablet/html/js/wc/storage/storage');
var cache = require('syracuse-tablet/html/js/wc/sdata/cache/sdataCache');

/**
 * 
 */
exports.init = function() {
	return $.smResolve()
		.then(function() {
			return _initComponentModules();
		});
};

/**
 * 
 * @returns
 */
function _initComponentModules() {
	return $.smResolve()
		.then(function() {
			modules.addModule("navHelper", navigationHelper);
			modules.addModule("navStack", navigationPageStack);
			modules.addModule("ctrlFactory", ctrlFactory);
			modules.addModule("pageFactory", pageFactory);
			modules.addModule("controller", controller);
			modules.addModule("modal", modal);
			modules.addModule("siteLayout", siteLayout);
			modules.addModule("appController", appController);
			modules.addModule("ajax", ajax);
			modules.addModule("session", session);
			modules.addModule("storage", storage);
			modules.addModule("cache", cache);
		});
}