	var _createHtml = function(cl, res) {
		if (cl.before) {
			res.push(cl.before);
			cl.before = null;
		}
		if (cl.html) {
			res.push(cl.html);
			cl.html = null;
		}
		if (cl.childs) {
			cl.childs.forEach(function(child) {
				_createHtml(child, res);
			});
		}
		if (cl.after) {
			res.push(cl.after);
			cl.after = null;
		}
	};
