	// Create layouts and controls and linked with parents
	var _createPage = function(controller, controls, html, options) {
		var old = controller._html || null,
			olddetailpagedata, oldPrefix;
		controller._html = html;
		if (options) {
			if (options.pageData) {
				olddetailpagedata = controller.detailpagedata || null;
				controller.detailpagedata = options.pageData;
			}
			if (options.prefix) {
				oldPrefix = controller.detPrefix || "";
				controller.detPrefix = options.prefix;
			}
			if (options.data)
				controller.setData('', options.data);
		}
		try {
			Object.keys(controls).forEach(function(id, index) {
				var c = controls[id];
				if (c.layout) {
					var parent = controls[c.parent];
					if (parent) {
						parent.data = parent.data || {};
						parent.data.hasChildren = true;
					}
				}
			});
			var emptyCellOption = null,
				propagate = ["$styles", "$theme", "$label"];
			Object.keys(controls).forEach(function(id, index) {
				var c = controls[id];
				var parent = html[c.parent] || html.root;
				// Control id : controller.prefix + prefix - Ex: F1_items for a
				// query list - representation2ui
				c.id = controller.prefix + id;
				if (id === "root") {
					// Nothing
				} else if (c.layout) {
					var myParent = controls[c.parent];
					if (myParent) $helpers.propagateMobileExt(c.data, myParent.data);
					/*
					 * if (myParent){ for (var i=0; i < propagate.length; i++){
					 * var prop = propagate[i]; if (myParent[prop] &&
					 * !c.$styles[prop]){ c[prop] = myParent[prop]; } } }
					 */
					emptyCellOption = c.layout == "RowLayout" ? c.data && c.data.$emptycell ? c.data.$emptycell : "withtitle" : null;
					// Layout controls - see jquery.smobile.layout.js
					var l = $.smobile.addLayout.apply(controller, [parent, c]);
					if (l.$inquiry != null && !controller.pageInfo.$inquiry) {
						// Set inquiry mode if there is at least one inquiry
						// bloc param or result
						// USed later to open
						controller.pageInfo.$inquiry = true;
					}
					l.parent = l.parent || c.parent || "root";
					if (l && l.event) {
						controller.layouts = controller.layouts || {};
						controller.layouts[c.id] = {
							id: c.id,
							event: l.event,
							options: l.options
						};
						l.event = null;
					}
					html[id] = l;
					if (l.childs && l.childs.length) {
						l.childs.forEach(function(child) {
							html[child.id] = child;
							child.parent = id;
						});
					}
				} else if (c.uiClass) {
					// UI Controls with handler, refresh, events methods - Class
					// defined in $.smobile.controls[c.uiClass]
					// Calls 'handler' method to initcontrol(create html);
					// Do not display empty cells in RowLayout #3128 - details
					// only
					var parentLyt = controls[c.parent];
					if (!c.data) c.data = {};
					if (!parentLyt && html[c.parent]) {
						// Tabs layout - Only one control c_001 per tab - Each
						// tab has c_001_0, _1 id but refers to c_001
						parentLyt = controls[html[c.parent]["parent"]];
					}
					if (parentLyt) {
						if (!c.data.$article) c.data.$article = {};
						$helpers.propagateMobileExt(c.data.$article, parentLyt.data);
					}
					c.emptyCellOption = emptyCellOption;
					$.smobile.addControl.apply(controller, [parent, c,
						function(ctrl) {
							// ctrl -> is not a class object it contains { html,
							// action, options}
							if (ctrl && Array.isArray(ctrl)) {
								ctrl.forEach(function(cc) {
									html[id + cc.scid] = cc;
									if (cc.bind || cc.action) {
										controller.registerControl({
											id: c.id + cc.scid,
											bind: cc.bind,
											action: cc.action,
											options: cc.options,
											uiClass: cc.uiClass,
											refresh: cc.refresh
										});
									}
								});
							} else if (ctrl) {
								if (c.emptyCellOption != null) {
									// We have to modify the class in html
									// string :-(
									var p = html[c.parent];
									if (p && p.before) {
										// Cell css is initialize by
										// layout.RowLayout method according to
										// $emptycell option
										// If a control is attached to the cells
										// we reset $emptycell option to
										// calculate the it according ctrl value
										p.before = p.before.replace(" empty", "");
										// Issue #3128 - do not display empty
										// cells or empty rows for RowLayouts
										if (ctrl.isEmpty || ctrl.html == null || ctrl.html.length == 0) {
											if (p.before.indexOf("s-m-l-row") >= 0) {
												// We add empty class in cell
												if (c.emptyCellOption == "empty") {
													p.before = p.before.replace("s-m-l-row", "s-m-l-row empty");
												}
												p = html[p.parent];
												if (p && p.childs && (p.nbEmpty != null || p.before.indexOf("s-m-l-table") >= 0)) {
													p.nbEmpty = p.nbEmpty == null ? 1 : p.nbEmpty + 1;
													if (p.nbEmpty == p.childs.length) {
														// We add empty class in
														// row
														p.before = p.before.replace("s-m-l-table", "s-m-l-table empty");
														delete p.nbEmpty;
													}
												}
											}
										}
									}
								}
								html[id] = ctrl;
								if (ctrl.bind || ctrl.action) {
									controller.registerControl({
										id: c.id,
										bind: ctrl.bind,
										action: ctrl.action,
										options: ctrl.options,
										uiClass: c.uiClass,
										refresh: ctrl.refresh
									});
								}
								if (ctrl.async) {
									// View control with asynchronous handler
									// called by bindData - See appView and
									// syncView
									controller.asyncs = controller.asyncs || [];
									controller.asyncs.push(ctrl.async);
									ctrl.async = null;
								}
							}
						}
					]);
				}
			});
		} finally {
			controller._html = old;
			if (options) {
				if (options.pageData)
					controller.detailpagedata = olddetailpagedata;
				if (options.prefix)
					controller.detPrefix = oldPrefix;
			}

		}
	};

