"use strict";
(function($) {
	if (!$.smobile) $.smobile = {};

	var Controller = function(page) {
		this._page = page;
	};
	Controller.prototype = {
		init: function() {},
		destroy: function() {
			this._page = null;
		}
	};

	$.smobile.createController = function(page) {
		var c = new Controller(page);
		c.init();
		return c;
	};

})(jQuery);