"use strict";

(function() {
	// logger for wrapper
	var _log = function() {
		console.log(arguments[0]);
	};

	// callable from inside webview
	var _logExternal = function(dataObject) {
		return new WinJS.Promise(
			function(resolve, reject, progress) {
				var params = dataObject && dataObject.args;
				console.log(params.join());
				resolve();
			});
	};

	window.$SAGE.log = _log;
	window.$SAGE.registerContainerAPI("logger", {
		log: _logExternal
	});
})();