"use strict";

var webdriver = require('selenium-webdriver');


var until = webdriver.until;
var By = webdriver.By;
var _browsers = ["chrome", "MicrosoftEdge"];
var _browserName = _browsers[0];

//var browser = new webdriver.Builder().usingServer().withCapabilities({'browserName': 'chrome' }).build();
var browser = new webdriver.Builder().usingServer().withCapabilities({
	'browserName': _browserName
}).build();

browser.get('http://localhost:8124/syracuse-tablet/dist/index.html');

var login_user = browser.wait(until.elementLocated(By.id('login-user')), 30000);
login_user.sendKeys("guest");
var login_password = browser.wait(until.elementLocated(By.id('login-password')), 30000);
login_password.sendKeys("guest");
var login_login = browser.wait(until.elementLocated(By.id('login-login')), 30000);
login_login.click();

//browser.quit();