"use strict";

var driver = require("./driver");

exports.sendKeysById = function(id, keys) {
	return driver.Browser().wait(driver.Until.elementLocated(driver.By.id(id)), 30000).sendKeys(keys);
};
exports.clickById = function(id) {
	return driver.Browser().wait(driver.Until.elementLocated(driver.By.id(id)), 30000).click();
};

exports.waitForPage = function(pageName) {
	return driver.Browser().wait(driver.Until.elementLocated(driver.By.className("s-m-page s-m-full welcome dashboard")), 30000)
		.then(function(page) {
			console.log(page);
		});
};