"use strict";

var UT = require("../ut");

describe("Something", function() {
	it("Login", function() {
		this.timeout(5000);
		return UT.Driver.Browser().get('http://localhost:8124/syracuse-tablet/dist/index.html').then(function() {
			return UT.DOM.sendKeysById('login-user', 'guest')
				.then(function() {
					return UT.DOM.sendKeysById('login-password', 'guest');
				})
				.then(function() {
					return UT.DOM.clickById('login-login');
				})
				.then(function() {
					//return waitForPage("");
				});
		});
	});
	after(function() {
		return UT.Driver.Browser().quit();
	});
});