"use strict";

var UT = require("../ut");

describe("Something", function() {

	it("Login", function() {
		this.timeout(51000);

		return UT.App.loadApp()
			.then(function() {
				return UT.App.ensureLogin();
			})
			.then(function() {
				return UT.App.waitPage("$welcomeDashboard.$mobileDashboard");
			});
	});

	after(function() {
		return UT.Driver.Browser().quit();
	});
});