"use strict";

var webdriver = require('selenium-webdriver');

var until = webdriver.until;
var By = webdriver.By;

//=============================================================================================
//======================= Run with Serer local Chrome driver                         ==========
// java -jar selenium-server-standalone-2.44.0.jar -Dwebdriver.chrome.driver=.\chromedriver.exe
//==============================================================================================
var expectedTitle = 'Sage X3 Mobile - Debug';

// --- connect to Server Local ------------------------------
var browser = new webdriver.Builder().
usingServer('http://localhost:4444/wd/hub').
withCapabilities(webdriver.Capabilities.chrome()).build();

browser.manage().window().maximize();

browser.get('http://localhost:8124/syracuse-tablet/html/index_debug.html');


//==================================== It works well with css  IE =============================================
var login_user = browser.wait(until.elementLocated(By.css('input[id="login-user"]')), 20000);
login_user.sendKeys("admin");
var login_password = browser.wait(until.elementLocated(By.css('input[id="login-password"]')), 20000);
login_password.sendKeys("admin");
var login_login = browser.wait(until.elementLocated(By.css('button[id="login-login"]')), 20000);
login_login.click();
//-- must have a time out
var tiles = browser.wait(until.elementLocated(By.xpath('//div[@id="_welcome_MOBILEMENUGENERALVig45"]')), 10000);
tiles.click();

// --- get title -------------------------------------
browser.getTitle().then(function(title) {
	if (expectedTitle === title) {
		console.log("Chrome - Verification Successful - The correct title is displayed on Chrome page.=" + title);
	} else {
		console.log("Verification Failed - An incorrect title is displayed on the web page.");
	}
});