"use strict";

var utils = require("./utils");
var sys = require('util');

var _log = console.log;

utils.start(true).then(function() {
	_log && _log("STARTED");
}).then(function() {
	_log && _log("END OK");
	return utils.stop();
}).thenCatch(function(e) {
	_log && _log("FAILED");
	_log && _log(sys.inspect(e, null, 2));
	utils.stop();
});