"use strict";

var UT = require("./../lib/ut");
// =======================================================================
// Navigation on the array page:  $next, $$previous, $last, $first -- ----
//= Logout
// =======================================================================
describe("AQMCRUDM query for navigation", function() {
	UT.defaultBeforeAndAfter({
		testPageUrl: "{$hostPort}/mobile2/x3/erp/GX3APP/AQMDEVICE?representation=AQMCRUDM.$query"
	});

	it("Navigation $next icon", function() {
		this.timeout(10000);
		return UT.App.pagination("$next");
	});

	it("Navigation $previous icon", function() {
		this.timeout(10000);
		return UT.App.pagination("$previous");
	});

	it("Navigation $last icon", function() {
		return UT.App.pagination("$last");
	});

	it("Navigation $first icon", function() {
		return UT.App.pagination("$first");
	});

	// ---------------------------------------------------------------------
	//------ Logout with span.fa.fa-bars: Burger  icon ---------------------
	// ---------------------------------------------------------------------
	it("Logout for navigation", function() {
		return UT.App.logout();
	});

});