"use strict";

exports.main = function() {
	var testModules = [
		require('../../test/js/storage/testStorage'),
		require('../../test/js/testAjax'),
		require('../../test/js/testGeoLoc')
	];

	try {
		var runTest = function() {
			module = testModules.pop();
			if (module) {
				module.test().then(function() {
					runTest();
				});
			}
		};
		runTest();
	} catch (e) {
		console.log(e);
	}
};