"use strict";

var testUtil = require('syracuse-tablet/test/js/testUtil');
var winjs = require('syracuse-tablet/html/js/helpers/winjs');

exports.test = function() {
	var deferred = new $.Deferred();

	var testset;
	var testcase;

	testset = testUtil.addTestset("Geo location test");
	testcase = testset.addTestcase("Get location");

	// Only test on real device
	if (!winjs.isAvailable()) {
		testcase.addResult("Service not available, test ended, this is not an error", true);
		deferred.resolve();
		return deferred.promise();
	}

	winjs.callWinJS("geoLoc", "getLocation").then(function(data) {
		testcase.addResult("Getting location", data.valid);
		if (data.valid) {
			var src = "https://www.google.com/maps?q=" + data.latitude + "," + data.longitude;
			var res = testcase.addResult(src, data.valid);
			$('<a href="' + src + '">' + src + '</a>').appendTo(res.$root);
		};
	}, function(error) {
		testcase.addResult("Error getting location", false, error);
	}).then(function() {
		deferred.resolve();
	});

	return deferred.promise();
};