"use strict";

var widgetsLibrary = require('syracuse-tablet/html/js/widgets/widgetsLibrary');

exports.main = function() {
	$("a[data-sm-menu]").bind("click", function(event) {

		// widget-test-fields"
		switch (event.target.getAttribute("data-sm-menu")) {
			case "widget-test-fields":
				var bodyLayout = document.getElementById("sm-site-body-layout");
				if (bodyLayout.children.length == 0) {
					// sample data
					var $item = {
						$title: "Test title"
					};
					var dataValueSample = "Test data";

					// init widget
					var item = widgetsLibrary.categories["field"].create();
					item.buildContainer($item);
					bodyLayout.appendChild(item.render());

					// set data value
					item.setDataValue(dataValueSample);
				}

				break;
			case "widget-test-diagnoses":
				break;
		}

	});
};