"use strict";
/// !doc
///
/// # Storage area logging  
/// 
var helpers = require('syracuse-core/lib/helpers');

// Default implementation for log tracing simply outputs all information directly to the console

exports.defLogger = helpers.defineClass(function(logType, description) {
	this.logType = logType;
	this.description = description;
}, null, {
	initialise: function(_) {},
	preprocess: function(_, data) {},
	trace: function(data) {
		console.log(data.output);
	},
	close: function(_) {}
});