"use strict";
/// !doc
///
/// # Storage area logging  
/// 
var helpers = require('syracuse-core/lib/helpers');

// Default implementation for log tracing simply outputs all information directly to the console

exports.defLogger = helpers.defineClass(function() {
	this.type = "console";
}, null, {
	initialise: function(_) {},
	preprocess: function(_, data) {},
	trace: function(data) {
		console.log(data.output);
	},
	close: function() {},
	toJSON: function() {
		return this.type;
	}
});