"use strict";

var Console = require('syracuse-trace/lib/devices/console').Console;
var Storagearea = require('syracuse-trace/lib/devices/storagearea').Storagearea;
var Mongodb = require('syracuse-trace/lib/devices/mongodb').Mongodb;
var File = require('syracuse-trace/lib/devices/file').File;

exports.newDevice = function(type, options) {
	switch (type) {
		case "storagearea":
			return new Storagearea(options);
		case "mongo":
			return new Mongodb(options);
		case "file":
			return new File(options);
		case "console":
			return new Console();
		default: // console
			return new Console();
	}
};