"use strict";
/// !doc
///
/// # Storage area logging  
/// 
var locale = require("syracuse-core/lib/locale");
var helpers = require('syracuse-core/lib/helpers');
var sa = require('syracuse-orm/lib/storageArea');
var sys = require('util');
var datetime = require('syracuse-core/lib/types/datetime');
var defLogger = require('syracuse-trace/lib/defLogger').defLogger;

// Standard callback replace _ callback to be able to use this transport in not streamlined functions
var cb = function(err, result) {
	if (err) throw err;
	return result;
};

exports.saLogger = helpers.defineClass(function(name, expiry) {
	this.name = name;
	this.expiry = expiry || 120;
	this.log = null;
	this.properties = {
		description: " traces (" + this.name + ")",
		content: {
			contentType: "text/plain",
			append: true,
			fileName: ""
		}
	};
	this.type = "storagearea";
}, defLogger, {
	_getLogFileName: function() {
		var self = this;
		var today = new Date();
		var yr = today.getYear();
		var mo = today.getMonth();
		var dy = today.getDay();
		var logFileName = "Trace_" + this.name + "_" + yr + "_" + mo + "_" + dy;
		if (self.log && self.properties.content.fileName !== fName) {
			this.close();
		}
		return logFileName;
	},
	initialise: function() {
		var self = this;
		if (!self.log) {
			var today = new Date();
			var fName = this._getLogFileName();
			var filter = {
				jsonWhere: {
					fileName: fName
				}
			};

			// The simple fact that the file name has altered will cause a new
			// entry to be created in the storage area.
			self.properties.content.fileName = fName;
			self.log = {};
			self.log.dd = sa.open(cb, filter, null, "serverLog");
			if (!self.log.dd) {
				console.log("Failed to open the serverLog file by fileName");
				self.log.dd = sa.open(cb, null, null, "serverLog");
			} else {
				console.log("Opened - " + self.log.dd);
			}
			if (!self.log.instance) {
				self.log.instance = sa.getDocumentInstanceByDD(cb, self.log.dd);
				self.log.instance.warnDiags(cb, [locale.format(module, "traceOpened")]);
			}

			if (!self.log.dd) {
				console.log("Failed to re-open the serverLog file");
			} else {
				console.log("Initialised - " + self.log.dd);
				sa.write(cb, self.log.dd, self.properties, "=== " + self.name + " Trace Initialised ===\n", true);
			}

			// Expires according to the parameter
			// Need to change this so that we expire according to the number of
			// days in the cycle
			// var dateExpire = new Date((new Date()).getTime() + this.expiry * 60 * 1000);
			// self.log.properties.expiration = datetime.fromJsDate(dateExpire);
		}
		return self.log;
	},
	preprocess: function(data) {},
	trace: function(data) {
		var self = this;
		try {
			var t = self.log;
			if (!t) {
				t = self.initialise();
			}
			if (t) {
				sa.write(cb, t.dd, self.properties, data.output + "\n", true);
			}
		} catch (e) {
			console.error("Error in trace: " + e.stack);
			throw e;
		}
	},
	close: function() {
		var self = this;
		if (self.log) {
			self.log.instance.warnDiags(cb, []);
			self.log.instance.infoDiags(cb, [locale.format(module, "traceClosed")]);
			sa.close(cb, self.log.dd, true);
			delete this.log;
		}
	},
	toJSON: function() {
		return this.type;
	}
});