"use strict";

var jsonTree = require('./jsonTree');

exports.create = function(opts) {
	return {
		setValue: function(val) {
			var mode = mode || 'indented'; //'collapsible';
			if (opts.facet === '$details') jsonTree.create(opts.div, mode).setValue(val);
			else $('<span>').text('<JSON data>').appendTo($(opts.div));
		},
	};
};